            {{ form_row(form.content) }}
        </div>
        <div class=\"other\">
            <div class=\"author input\">
                {{ form_row(form.id_author) }}
            </div>
            <div class=\"thumbnail input\">
                {{ form_row(form.thumbnail) }}
            </div>
