            <a href=\"{{ path('admin.blog.index') }}\" class=\"btn-red input\">Annuler <i class=\"fa-solid fa-xmark\" style=\"color: #ffffff;\"></i></a>
        </div>
        {{ form_end(form) }}
    </div>

